   <template>
  <li
    class="home-card"
    @click="$router.push({ path: '/playlist', query: { id: item.id } })"
  >
    <span> {{ item.playCount | moneyFormat }}</span>
    <img :src="`${item.picUrl}?imageView=1&type=webp&thumbnail=370x0`" alt="" />
    <h5>{{ item.name }}</h5>
  </li>
</template>
   
   <script>
export default {
  props: {
    item: Object,
  },
  filters: {
    //取截单元,单位
    moneyFormat: function (arg) {
      if (arg.toString().length >= 13) {
        // return arg/1000000000000+"万亿"
        const moneys = arg / 1000000000000;
        const realVal = parseFloat(moneys).toFixed(2);
        return realVal + "万亿";
      } else if (arg.toString().length >= 9) {
        const moneys = arg / 100000000;
        const realVal = parseFloat(moneys).toFixed(2);
        return realVal + "亿";
      } else if (arg.toString().length >= 4) {
        const moneys = arg / 10000;
        const realVal = parseFloat(moneys).toFixed(1);
        return realVal + "万";
      }
    },
  },
};
</script>
   
